select
         distinct cpe.entity_id as product_id,
       tre.product as product_title,
        ( select concat('https://www.kttape.com/',ur.request_path) as product_url from url_rewrite ur where ur.entity_id=cpe.entity_id and ur.entity_type='product'  limit  1) as product_url,
        tre.created as date,
          rd.detail as review_content,
                        (select res.rating_summary from review_entity_summary res where res.rating_summary=tre.id limit 1) as review_score,
          tre.title as review_title,
          tre.nickname as display_name,
       (select ce.email from customer_entity ce where ce.entity_id = rd.customer_id limit 1) as email,
       'US' as md_customer_country
from temp_reviews_export tre
inner join review_detail rd on tre.id=rd.review_id
inner join catalog_product_entity cpe on tre.sku=cpe.sku
inner join              catalog_product_entity_varchar  cpev  on cpe.row_id=cpev.row_id  and cpev.attribute_id=73
