select          distinct  r.review_id,          res.rating_summary as review_score,
                         tre.sku,
cpe.entity_id as product_id,
cpev.value as product_title ,
( select concat('https://www.kttape.com/',ur.request_path) as product_url from url_rewrite ur where ur.entity_id=cpe.entity_id and ur.entity_type='product'  limit  1) as product_url,
r.created_at as date2,
rd.detail as review_content,
(
    CASE
        WHEN (res.rating_summary=0)  THEN 0
        WHEN (res.rating_summary=38 or res.rating_summary=46 or res.rating_summary=60 or res.rating_summary=72 or res.rating_summary=73)  THEN 1
        WHEN (res.rating_summary=30)  THEN 2
        WHEN (res.rating_summary=82)  THEN 3
        WHEN (res.rating_summary=70 or res.rating_summary=80 or res.rating_summary=89 or res.rating_summary=95 or res.rating_summary=97)  THEN 4
        WHEN (res.rating_summary=74 or res.rating_summary=85 or res.rating_summary=86 or res.rating_summary=90 or res.rating_summary=93 or res.rating_summary=100)  THEN 5
    END
)   as review_score,
tre.title as review_title,
tre.nickname as display_name,
(select ce.email from customer_entity ce where ce.entity_id = rd.customer_id limit 1) as email,
'US' as md_customer_country
from temp_reviews_export tre
inner join review r on tre.id=r.review_id
inner join review_detail rd on r.review_id = rd.review_id
